mapper.select:
SELECT bid, title, content, view_cnt, reg_id, reg_nickname,
IF ( DATE_FORMAT(reg_dt, '%Y%m%d') < DATE_FORMAT(now(),'%Y%m%d'), DATE_FORMAT(reg_dt, '%Y-%m-%d'), DATE_FORMAT(reg_dt, '%H:%i') ) as reg_dt,
edit_dt, bg_no, reply_view_cnt, recommend_cnt, notice FROM tbl_board WHERE bid = #{bid}

mapper.update:
UPDATE tbl_board SET
	reply_view_cnt = reply_view_cnt + #{amount}
WHERE
	bid = #{bid}
